PRAGMA foreign_keys=OFF;
DROP TABLE IF EXISTS book;
DROP TABLE IF EXISTS template;
DROP TABLE IF EXISTS obstacle;
DROP TABLE IF EXISTS leaderboard;
DROP TABLE IF EXISTS doctrine_migration_versions;
DROP TABLE IF EXISTS messenger_messages;
BEGIN TRANSACTION;
CREATE TABLE doctrine_migration_versions (version VARCHAR(191) NOT NULL, executed_at DATETIME DEFAULT NULL, execution_time INTEGER DEFAULT NULL, PRIMARY KEY(version));
CREATE TABLE book (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, title VARCHAR(255) NOT NULL, isbn VARCHAR(255) DEFAULT NULL, author VARCHAR(255) NOT NULL, image_url VARCHAR(255) DEFAULT NULL);
CREATE TABLE messenger_messages (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, body CLOB NOT NULL, headers CLOB NOT NULL, queue_name VARCHAR(190) NOT NULL, created_at DATETIME NOT NULL, available_at DATETIME NOT NULL, delivered_at DATETIME DEFAULT NULL);
CREATE TABLE obstacle (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, name VARCHAR(255) NOT NULL, description VARCHAR(255) NOT NULL, difficulty_int INTEGER DEFAULT NULL, difficulty_str INTEGER DEFAULT NULL, difficulty_dex INTEGER DEFAULT NULL, cost_reward_time INTEGER NOT NULL, cost_reward_health INTEGER NOT NULL, cost_reward_stamina INTEGER NOT NULL, cost_reward_int INTEGER NOT NULL, cost_reward_str INTEGER NOT NULL, cost_reward_dex INTEGER NOT NULL, cost_reward_lck INTEGER NOT NULL, cost_reward_spd INTEGER NOT NULL, cost_reward_con INTEGER NOT NULL);
CREATE TABLE template (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, name VARCHAR(255) DEFAULT NULL);
CREATE TABLE leaderboard (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, player VARCHAR(255) NOT NULL, challenger VARCHAR(255) NOT NULL, distance INTEGER NOT NULL);
DELETE FROM sqlite_sequence;
CREATE INDEX IDX_75EA56E0FB7336F0 ON messenger_messages (queue_name);
CREATE INDEX IDX_75EA56E0E3BD61CE ON messenger_messages (available_at);
CREATE INDEX IDX_75EA56E016BA31DB ON messenger_messages (delivered_at);
COMMIT;
